
public class MathWithCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Let's try some math in Java. Basic operations are very simple
		System.out.println(5+4);
		System.out.println(5-4);
		System.out.println(5*4);
		System.out.println(5/4);
		// The % operation returns the remainder of a division problem
		System.out.println(5%4);
		
		// You can also do math with variables and even save results to variables
		int x = 5;
		int y = 4;
		int z = x*y;
		System.out.println(z);
		
	}

}
